import { createStore } from "redux";
import { createSlice /* , createReducer*/ } from "@reduxjs/toolkit";

const initialState = {
  counter: 0,
  showCounter: true,
};

createSlice({
  name: "counter",
  initialState,
  reducers: {
    // Here we ARE allowed to edit the original state, unlike in the counterReducer function
    // Actually, it only looks to us that we are modifying the original state, however redux toolkit handles that in the background for us
    decrement(state) {
      state.counter++;
    },
    increment(state) {
      state.counter--;
    },
    increase() {},
    toggle() {},
  },
});

const counterReducer = (prevState = initialState, action) => {
  if (action.type === "decrement") {
    return {
      counter: prevState.counter - 1,
      showCounter: prevState.showCounter,
    };
  }
  if (action.type === "increment") {
    return {
      counter: prevState.counter + 1,
      showCounter: prevState.showCounter,
    };
  }
  if (action.type === "increase") {
    return {
      counter: prevState.counter + action.amount,
      showCounter: prevState.showCounter,
    };
  }
  if (action.type === "toggle") {
    return {
      showCounter: !prevState.showCounter,
      counter: prevState.counter,
    };
  }

  return prevState;
};

const store = createStore(counterReducer);

export default store;
